var modal_open_button=document.querySelectorAll(".itemcard__cart"),modal_window=document.querySelector(".cart-popup");if(modal_open_button.length>0)for(var i=0;i<modal_open_button.length;i++)modal_open_button[i].addEventListener("click",function(o){o.preventDefault(),modal_window.classList.add("cart-popup--show")});window.addEventListener("keydown",function(o){27==o.keyCode&&modal_window.classList.remove("cart-popup--show")});